 {object oPlayer = GetPCSpeaker();
 if (!GetIsPC(oPlayer)) return;
//LockCameraDirection(oPlayer);
 LockCameraDistance(oPlayer);
 LockCameraPitch(oPlayer);
 int bLocked=TRUE;
 StoreCameraFacing();}
